# example of program that calculates the total number of times each word has been tweeted.import collectionsimport operatorimport sys# file handlinginput_file = sys.argv[1]out_file = sys.argv[2] words = collections.Counter()# collect unique wordsfor line in open(input_file):    words.update(line.split())# sort wordssorted_words = sorted(words.iteritems(), key=operator.itemgetter(0), reverse=False)# get the correct formatting column widthlformat = len(max(words, key=len)) + 4# print output to filef = open(out_file, 'w')for word, count in sorted_words:    f.write(word.ljust(lformat) + " {}\n".format(count))f.close()